
/**
 * Module dependencies.
 */

var connect = require('./')
  , http = require('http')
  , fs = require('fs');

connect()
  .use(connect.logger(':req[accept] -> :res[content-type]'))
  .use(connect.favicon())
  .use('/connect', connect.static(__dirname + '/lib'))
  .use('/connect', connect.directory(__dirname + '/lib'))
  .use(function(req, res, next){
    var body = 'OK';
    res.setHeader('Content-Length', body.length);
    res.end(body);
  })
  .listen(3000);
